require 'rubygems'require 'opengl'require 'glut'require 'rewclib'WIDTH = 640HEIGHT = 480FPS = 30$cam = Rewclib.newunless $cam.open(WIDTH, HEIGHT, FPS)  puts 'No camera'  exit(0)enddisplay = proc {  glClear(GL_COLOR_BUFFER_BIT)  data = $cam.image  glDrawPixels(WIDTH, HEIGHT, GL_RGB, GL_UNSIGNED_BYTE, data)  glutSwapBuffers}idle = proc {  glutPostRedisplay}glutInitglutInitDisplayMode(GLUT_RGB | GLUT_DOUBLE | GLUT_ALPHA | GLUT_DEPTH)glutInitWindowSize(WIDTH, HEIGHT)glutCreateWindow('Webcam')glutDisplayFunc(display)glutIdleFunc(idle)glClearColor(0.5, 0.5, 0.5, 0.0)glutMainLoop$cam.close